import { useEffect, useState } from 'react';

const ProfileImgContainer = () => {
    var profileId = 1;
    const [userDesc, setUserDesc] = useState([]);

    const getUserDescription = () => {
        fetch(`http://localhost:5432/myportfolio/${profileId}`)
		.then(response => {
			if (!response.ok) {
			  	throw new Error('Network response was not ok');
			}
			return response.json();
		})
        .then(data => {
			console.log('Successfully got user description');
            setUserDesc(data);
        })
		.catch(error => {
			console.error("Error fetching data: ", error);
		});
    }

    useEffect(() => {
		getUserDescription();
    }, []);

    return (
		<div id="profileImgContainer">
            <img src={"https://avatars.githubusercontent.com/u/1"} id="profileImg" alt="Profile Image" className='rounded-circle' /> 

            <div id="userBio">
                <p id="username">{userDesc["firstname"] + " " + userDesc["lastname"]}</p>
                <p id="userOccupation">{}</p>
            </div>
        </div>

		// <div>
		// 	<h1>Data from PostgreSQL Database for Profile: {profileId}</h1>

		// 	<ul>
		// 		{userDesc.map(item => (
		// 			<li key={item.id}>{item.value}</li>
		// 		))}
		// 	</ul>

		// 	<Button onClick={getUserDescription()}>Click Me</Button>
		// </div>
    );
}

export default ProfileImgContainer;
