const ProfileImgContainer = () => {
    var profileId = 1;

    // const [data, setData] = useState(["", "John Doe", "Doctor"]);
    const [userDesc, setUserDesc] = useState(false);

    function getUserDescription() {
        fetch(`http://localhost:5432/myportfolio/${profileId}`)
        .then(response => {
            return response.text();
        })
        .then(data => {
            setUserDesc(data);
        });
    }

    useEffect(() => {
        getUserDescription();
    }, []);

    return (
