import { useEffect, useState } from 'react';

import axios from 'axios';

const ProfileImgContainer = () => {
    var profileId = 1;
    const [userDesc, setUserDesc] = useState([]);

    // function getUserDescription() {
    //     fetch(`http://localhost:5432/myportfolio/${profileId}`)
    //     .then(response => {
    //         return response.text();
    //     })
    //     .then(data => {
    //         setUserDesc(data);
    //     });
    // }

    useEffect(() => {
        axios.get(`http://localhost:5432/myportfolio/${profileId}`)
		.then(response => {
			setUserDesc(response.data);
		})
		.catch(error => {
			console.error("Error fetching data: ", error);
		})
    }, [profileId]);

    return (
		// <div id="profileImgContainer">
        //     <img src={"https://avatars.githubusercontent.com/u/1"} id="profileImg" alt="Profile Image" className='rounded-circle' /> 

        //     <div id="userBio">
        //         <p id="username">{userDesc["firstname"] + " " + userDesc["lastname"]}</p>
        //         <p id="userOccupation">{userDesc["occupation"]}</p>
        //     </div>
        // </div>

		<div>
			<h1>Data from PostgreSQL Database for Profile: {profileId}</h1>

			<ul>
				{userDesc.map(item => (
					<li key={item.id}>{item.value}</li>
				))}
			</ul>
		</div>
    );
}

export default ProfileImgContainer;
