const { Pool } = require('pg');

const pool = new Pool({
    host: 'localhost',
    user: 'postgres',
    database: 'postgres_db',
    password: 'password',
    port: 5432,
});

const getUserDescription = async (id) => {
    try {
        return await new Promise(function (resolve, reject) {
            pool.query(`SELECT * FROM profiles WHERE profileId = ${id}`, (error, results) => {
                if (error) {
                    reject(error);
                }
                if (results && results.rows) {
                    resolve(results.rows);
                } else {
                    reject(new Error("No results found"));
                }
            });
        });
    } catch (error_1) {
        console.error(error_1);
        throw new Error("Internal server error");
    }
};

module.exports = {
    getUserDescription
};
